// scroll away address bar
function showSearch() {
    $("header").animate({
        height: "20px",
        background: "#231f20"
    }, 350, "ease-out");
    $(".search-box").css("background", "#fff");
    $(".search-box-container").append('<div class="x-btn"></div>');
    $(document).on("tap", ".x-btn", function(e) {
        e.preventDefault();
        $(".search-box").blur();
    });
}

function hideSearch() {
    $("header").animate({
        height: "63px",
        background: "#fff"
    }, 350, "ease-in");
    var e = "../img/carot.png'";
    $(".search-box").css("background", "url(" + e + ") no-repeat");
    $(".x-btn").hide(function() {});
}

window.addEventListener("load", function() {
    setTimeout(function() {
        window.scrollTo(0, 1);
    }, 0);
});

$(".search-box").focus(function() {
    showSearch();
});

$(".search-box").blur(function() {
    hideSearch();
});

$("#frmSearch").submit(function(e) {
    hideSearch();
});